class Map:    def __init__(self):        self.size = (10, 10)        self.thing = [0, 0]        self.end = (9, 9)        self.keys = {            "s": (1, 0),            "z": (-1, 0),            "d": (0, 1),            "q": (0, -1)            # "a": (-1, -1),            # "e": (-1, 1),            # "w": (1, -1),            # "c": (1, 1)        }    def print_map(self):        for i in range(0, self.size[0]):            for j in range(0, self.size[1]):                print("|", end="")                if i == self.thing[0] and j == self.thing[1]:                    print("@", end="")                elif (i, j) == self.end:                    print("#", end="")                else:                    print(" ", end="")            print("|",)    def use_key(self, key: str):        if key not in self.keys:            return        self.thing[0] += self.keys[key][0]        self.thing[1] += self.keys[key][1]        for i in range(0, 2):            if self.thing[i] < 0:                self.thing[i] = self.size[i] - 1            if self.thing[i] > self.size[i]:                self.thing[i] = 0    def exit(self):        if (self.end[0] == self.thing[0] and self.end[1] == self.thing[1]):            return True        else:            return False